    public partial class Form1 : Form
    {
        public Form1()
        {
            InitializeComponent();

        }
        static string GetStringForSSID(Wlan.Dot11Ssid ssid)
        {
            return Encoding.ASCII.GetString(ssid.SSID, 0, (int)ssid.SSIDLength);
        }
        private void Form1_Load(object sender, EventArgs e)
        {
            WlanClient client = new WlanClient();
            label1.Text = (&quot;Initialized&quot;);
            foreach (WlanClient.WlanInterface wlanIface in client.Interfaces)
            {
                label1.Text = (&quot;Iterfaces&quot;);
                Wlan.WlanAvailableNetwork[] networks = wlanIface.GetAvailableNetworkList(0);
                foreach (Wlan.WlanAvailableNetwork network in networks)
                {
                    label1.Text = (&quot;Found networks&quot;);
                    if (network.dot11DefaultCipherAlgorithm == Wlan.Dot11CipherAlgorithm.WPA_UseGroup)
                    {
                        label1.Text = (&quot;Found WPA&quot;);
                    } if (network.dot11DefaultCipherAlgorithm == Wlan.Dot11CipherAlgorithm.WEP)
                    {
                        String n=&quot;&quot;;
                        n = network.dot11Ssid.SSID.ToString();
                        listBox1.Items.Add(n);
                        label1.Text = (&quot;Found WEP network with SSID&quot;);
                    }

                }
            }

        }
    }
}
